mapper.update:
UPDATE OVERTIME SET DEL_YN = 'Y'
DEL_YN = 'N' AND NO IN
#{no}

mapper.select:
SELECT A.NO , A.THISDATE , A.EMP_NO , B.NAME NAME, B.DEPT_CODE, C.NAME DNAME, D.NAME PNAME, A.TYPE, A.WORK_HOUR FROM OVERTIME A JOIN EMPLOYEE B ON(A.EMP_NO = B.NO) JOIN DEPARTMENT C ON (B.DEPT_CODE = C.NO) JOIN POSITION D ON (B.POSITION_CODE = D.NO) WHERE A.DEL_YN='N'
AND A.THISDATE =#{searchVo.searchYear}
AND B.DEPT_CODE =#{searchVo.searchOption}
AND ${searchVo.searchType} =#{searchVo.searchValue}
ORDER BY A.NO DESC OFFSET #{pvo.offset} ROWS FETCH NEXT #{pvo.boardLimit} ROWS ONLY

SELECT E.NO, PROFILE_IMAGE, PWD, E.NAME, BIRTH, GENDER, EMAIL, PHONE, EMERGENCY_PHONE, ADDRESS, DEPT_CODE, D.NAME AS DNAME, POSITION_CODE, P.NAME AS PNAME, SALARY, BANK_CODE, ACCOUNT_NO, TOTAL_VACATION_DAYS, STATUS_CODE, ES.NAME AS ESNAME, ENTER_DATE, OUT_DATE, ENROLL_DATE, MODIFY_DATE, DEL_YN FROM EMPLOYEE E JOIN DEPARTMENT D ON(E.DEPT_CODE = D.NO) JOIN POSITION P ON(E.POSITION_CODE = P.NO) JOIN EMP_STATUS ES ON(E.STATUS_CODE =ES.NO) WHERE 1=1 AND DEL_YN='N'
AND E.NO = #{searchEmpNo}
AND E.NAME = #{searchEname}
